syntax:
// Explicit type declaration
int age = 25;
String name = 'John';
bool isStudent = true;

// Type inference with var
var city = 'New York';
var temperature = 23.5;

example:
void main() {
  // Numbers
  int age = 21;
  double height = 5.9;

  // Strings
  String name = 'Alice';
  var greeting = 'Hello';

  // Boolean
  bool isStudent = true;

  // Print variables
  print('Name: \$name');
  print('Age: \$age');
  print('Height: \$height');
  print('Is Student: \$isStudent');
}

void main() {
  int a = 10;
  int b = 3;

  // Arithmetic operators
  print('Addition: \${a + b}');
  print('Subtraction: \${a - b}');
  print('Multiplication: \${a * b}');
  print('Division: \${a / b}');
  print('Modulus: \${a % b}');

  // Relational operators
  print('a > b: \${a > b}');
  print('a == b: \${a == b}');

  // Logical operators
  bool x = true;
  bool y = false;
  print('x && y: \${x && y}');
  print('x || y: \${x || y}');
}

syntax:
// This is a single-line comment

/*
This is a multi-line comment
that can span multiple lines
*/

/// This is a documentation comment
/// Used for documenting functions and classes

example:
void main() {
  // This is a single-line comment
  int age = 25; // Variable declaration with comment

  /*
  This is a multi-line comment
  explaining the following code block
  */
  String name = 'John';

  /// This function prints a greeting
  /// Takes a name parameter
  void greet(String name) {
    print('Hello, \$name!');
  }

  greet(name);
}

syntax:
// if statement
if (condition) {
  // code to execute
}

// if-else statement
if (condition) {
  // code if true
} else {
  // code if false
}

// switch statement
switch (variable) {
  case value1:
    // code
    break;
  case value2:
    // code
    break;
  default:
    // default code
}

example:
void main() {
  int age = 18;
  String grade = 'A';

  // if-else statement
  if (age >= 18) {
    print('You are an adult');
  } else {
    print('You are a minor');
  }

  // switch statement
  switch (grade) {
    case 'A':
      print('Excellent!');
      break;
    case 'B':
      print('Good!');
      break;
    case 'C':
      print('Average');
      break;
    default:
      print('Need improvement');
  }
}

syntax:
// for loop
for (int i = 0; i < 5; i++) {
  // code to repeat
}

// while loop
while (condition) {
  // code to repeat
}

// do-while loop
do {
  // code to repeat
} while (condition);

// for-in loop
for (var item in collection) {
  // code for each item
}

example:
void main() {
  // for loop
  print('For loop:');
  for (int i = 1; i <= 3; i++) {
    print('Count: \$i');
  }

  // while loop
  print('\nWhile loop:');
  int count = 1;
  while (count <= 3) {
    print('Count: \$count');
    count++;
  }

  // for-in loop
  print('\nFor-in loop:');
  List<String> fruits = ['apple', 'banana', 'orange'];
  for (String fruit in fruits) {
    print('Fruit: \$fruit');
  }
}

syntax:
// Function with return type
returnType functionName(parameters) {
  // function body
  return value;
}

// Function without return value
void functionName(parameters) {
  // function body
}

// Arrow function
returnType functionName(parameters) => expression;

example:
void main() {
  // Call functions
  greet('Alice');
  int sum = add(5, 3);
  print('Sum: \$sum');

  // Arrow function
  int multiply = (int a, int b) => a * b;
  print('Product: \${multiply(4, 6)}');
}

// Function without return value
void greet(String name) {
  print('Hello, \$name!');
}

// Function with return value
int add(int a, int b) {
  return a + b;
}

syntax:
// Positional parameters
void functionName(type param1, type param2) {}

// Named parameters (optional by default)
void functionName({type param1, type param2}) {}

// Required named parameters
void functionName({required type param1, required type param2}) {}

// Optional positional parameters
void functionName(type param1, [type param2]) {}

example:
void main() {
  // Positional parameters
  greet('Alice', 25);

  // Named parameters
  introduce(name: 'Bob', age: 30, city: 'New York');

  // Required named parameters
  calculate(required: 10, optional: 5);

  // Optional positional parameters
  printInfo('Charlie');
  printInfo('David', 28);
}

// Positional parameters
void greet(String name, int age) {
  print('Hello \$name, you are \$age years old');
}

// Named parameters
void introduce({String? name, int? age, String? city}) {
  print('Name: \$name, Age: \$age, City: \$city');
}

// Required named parameters
void calculate({required int required, int? optional}) {
  print('Required: \$required, Optional: \$optional');
}

// Optional positional parameters
void printInfo(String name, [int? age]) {
  print('Name: \$name, Age: \$age');
}

syntax:
// Create a list
List<type> listName = [];
var listName = <type>[];

// Initialize with values
List<type> listName = [value1, value2, value3];

// Access elements
listName[index]

// Add elements
listName.add(value);
listName.insert(index, value);

// Remove elements
listName.remove(value);
listName.removeAt(index);

example:
void main() {
  // Create and initialize a list
  List<String> fruits = ['apple', 'banana', 'orange'];

  // Access elements
  print('First fruit: \${fruits[0]}');
  print('List length: \${fruits.length}');

  // Add elements
  fruits.add('grape');
  fruits.insert(1, 'mango');

  // Print all fruits
  print('All fruits:');
  for (String fruit in fruits) {
    print('- \$fruit');
  }

  // Remove elements
  fruits.remove('banana');
  print('After removing banana: \$fruits');
}

syntax:
// Create a set
Set<type> setName = {};
var setName = <type>{};

// Initialize with values
Set<type> setName = {value1, value2, value3};

// Add elements
setName.add(value);

// Remove elements
setName.remove(value);

// Check membership
setName.contains(value);

example:
void main() {
  // Create a set
  Set<String> colors = {'red', 'green', 'blue'};

  // Add elements
  colors.add('yellow');
  colors.add('red'); // Won't add duplicate

  print('Colors: \$colors');
  print('Number of colors: \${colors.length}');

  // Check membership
  print('Has red: \${colors.contains('red')}');
  print('Has purple: \${colors.contains('purple')}');

  // Remove elements
  colors.remove('green');
  print('After removing green: \$colors');

  // Set operations
  Set<String> primaryColors = {'red', 'blue', 'yellow'};
  Set<String> secondaryColors = {'green', 'orange', 'purple'};

  Set<String> allColors = primaryColors.union(secondaryColors);
  print('All colors: \$allColors');
}

syntax:
// Create a map
Map<keyType, valueType> mapName = {};
var mapName = <keyType, valueType>{};

// Initialize with values
Map<keyType, valueType> mapName = {
  'key1': value1,
  'key2': value2,
};

// Access values
mapName['key']

// Add/update values
mapName['key'] = value;

// Remove values
mapName.remove('key');

example:
void main() {
  // Create a map
  Map<String, int> ages = {
    'Alice': 25,
    'Bob': 30,
    'Charlie': 22,
  };

  // Access values
  print('Alice\'s age: \${ages['Alice']}');
  print('Bob\'s age: \${ages['Bob']}');

  // Add new entries
  ages['David'] = 28;
  ages['Alice'] = 26; // Update existing

  // Print all entries
  print('\nAll ages:');
  ages.forEach((name, age) {
    print('\$name: \$age years old');
  });

  // Check if key exists
  print('\nHas Eve: \${ages.containsKey('Eve')}');

  // Remove entry
  ages.remove('Charlie');
  print('After removing Charlie: \$ages');
}
