void main() {
  Calculator calc = Calculator();

  // Example usage
  print('Addition: \${calc.add(5, 3)}');
  print('Subtraction: \${calc.subtract(10, 4)}');
  print('Multiplication: \${calc.multiply(6, 7)}');
  print('Division: \${calc.divide(15, 3)}');
}

class Calculator {
  double add(double a, double b) => a + b;
  double subtract(double a, double b) => a - b;
  double multiply(double a, double b) => a * b;
  double divide(double a, double b) {
    if (b == 0) throw Exception('Division by zero!');
    return a / b;
  }
}

void main() {
  GradeCalculator calculator = GradeCalculator();

  // Add assignment scores
  calculator.addAssignment('Quiz 1', 85, 0.2);
  calculator.addAssignment('Midterm', 92, 0.3);
  calculator.addAssignment('Final', 88, 0.5);

  // Calculate and display final grade
  double finalGrade = calculator.calculateFinalGrade();
  String letterGrade = calculator.getLetterGrade(finalGrade);

  print('Final Grade: \${finalGrade.toStringAsFixed(2)}%');
  print('Letter Grade: \$letterGrade');
}

class GradeCalculator {
  List<Assignment> assignments = [];

  void addAssignment(String name, double score, double weight) {
    assignments.add(Assignment(name, score, weight));
  }

  double calculateFinalGrade() {
    double total = 0;
    for (var assignment in assignments) {
      total += assignment.score * assignment.weight;
    }
    return total;
  }

  String getLetterGrade(double grade) {
    if (grade >= 90) return 'A';
    if (grade >= 80) return 'B';
    if (grade >= 70) return 'C';
    if (grade >= 60) return 'D';
    return 'F';
  }
}

class Assignment {
  String name;
  double score;
  double weight;

  Assignment(this.name, this.score, this.weight);
}

void main() {
  TodoList todoList = TodoList();

  // Add some tasks
  todoList.addTask('Learn Dart basics');
  todoList.addTask('Complete Flutter tutorial');
  todoList.addTask('Build a mobile app');

  // Mark a task as complete
  todoList.completeTask(0);

  // Display all tasks
  todoList.displayTasks();

  // Remove a task
  todoList.removeTask(1);

  print('\nAfter removing a task:');
  todoList.displayTasks();
}

class TodoList {
  List<Task> tasks = [];

  void addTask(String description) {
    tasks.add(Task(description));
    print('Task added: \$description');
  }

  void completeTask(int index) {
    if (index >= 0 && index < tasks.length) {
      tasks[index].isCompleted = true;
      print('Task completed: \${tasks[index].description}');
    }
  }

  void removeTask(int index) {
    if (index >= 0 && index < tasks.length) {
      String description = tasks[index].description;
      tasks.removeAt(index);
      print('Task removed: \$description');
    }
  }

  void displayTasks() {
    print('\n=== TO-DO LIST ===');
    for (int i = 0; i < tasks.length; i++) {
      String status = tasks[i].isCompleted ? '[âœ“]' : '[ ]';
      print('\${i + 1}. \$status \${tasks[i].description}');
    }
  }
}

class Task {
  String description;
  bool isCompleted;
  DateTime createdAt;

  Task(this.description) : isCompleted = false, createdAt = DateTime.now();
}

import 'dart:io';
import 'dart:math';

void main() {
  NumberGuessingGame game = NumberGuessingGame();
  game.play();
}

class NumberGuessingGame {
  Random random = Random();
  int targetNumber = 0;
  int attempts = 0;
  int maxAttempts = 7;

  void play() {
    print('Welcome to the Number Guessing Game!');
    print('I\'m thinking of a number between 1 and 100.');
    print('You have \$maxAttempts attempts to guess it.\n');

    targetNumber = random.nextInt(100) + 1;
    attempts = 0;

    while (attempts < maxAttempts) {
      print('Attempt \${attempts + 1}/\$maxAttempts');
      print('Enter your guess: ');

      try {
        int guess = int.parse(stdin.readLineSync()!);
        attempts++;

        if (guess == targetNumber) {
          print('ðŸŽ‰ Congratulations! You guessed it in \$attempts attempts!');
          return;
        } else if (guess < targetNumber) {
          print('Too low! Try a higher number.\n');
        } else {
          print('Too high! Try a lower number.\n');
        }
      } catch (e) {
        print('Please enter a valid number.\n');
        attempts--; // Don't count invalid input as an attempt
      }
    }

    print('Game over! The number was \$targetNumber.');
    print('Better luck next time!');
  }
}

void main() {
  BankAccount account = BankAccount('John Doe', '123456789', 1000.0);

  print('Account Holder: \${account.accountHolder}');
  print('Account Number: \${account.accountNumber}');
  print('Initial Balance: \${account.getBalance()}');

  // Perform transactions
  account.deposit(500.0);
  account.withdraw(200.0);
  account.withdraw(1500.0); // This should fail

  // Display transaction history
  account.displayTransactionHistory();
}

class BankAccount {
  String accountHolder;
  String accountNumber;
  double _balance;
  List<Transaction> _transactions = [];

  BankAccount(this.accountHolder, this.accountNumber, this._balance);

  double getBalance() => _balance;

  bool deposit(double amount) {
    if (amount > 0) {
      _balance += amount;
      _transactions.add(Transaction('Deposit', amount, DateTime.now()));
      print('Deposited \$${amount.toStringAsFixed(2)}. New balance: \$${_balance.toStringAsFixed(2)}');
      return true;
    }
    print('Invalid deposit amount');
    return false;
  }

  bool withdraw(double amount) {
    if (amount > 0 && amount <= _balance) {
      _balance -= amount;
      _transactions.add(Transaction('Withdrawal', -amount, DateTime.now()));
      print('Withdrew \$${amount.toStringAsFixed(2)}. New balance: \$${_balance.toStringAsFixed(2)}');
      return true;
    }
    print('Insufficient funds or invalid amount');
    return false;
  }

  void displayTransactionHistory() {
    print('\n=== TRANSACTION HISTORY ===');
    for (var transaction in _transactions) {
      print('\${transaction.date}: \${transaction.type} \$${transaction.amount.abs().toStringAsFixed(2)}');
    }
  }
}

class Transaction {
  String type;
  double amount;
  DateTime date;

  Transaction(this.type, this.amount, this.date);
}

void main() {
  String text = 'The quick brown fox jumps over the lazy dog. The dog was not impressed.';

  TextAnalyzer analyzer = TextAnalyzer(text);

  print('=== TEXT ANALYSIS ===');
  print('Original text: \$text\n');

  print('Word count: \${analyzer.getWordCount()}');
  print('Character count (with spaces): \${analyzer.getCharacterCountWithSpaces()}');
  print('Character count (without spaces): \${analyzer.getCharacterCountWithoutSpaces()}');
  print('Average word length: \${analyzer.getAverageWordLength().toStringAsFixed(2)}');

  print('\nMost frequent words:');
  analyzer.getMostFrequentWords(5).forEach((word, count) {
    print('\$word: \$count times');
  });
}

class TextAnalyzer {
  String text;
  List<String> words;

  TextAnalyzer(this.text) {
    // Clean and split text into words
    words = text.toLowerCase()
        .replaceAll(RegExp(r'[^\w\s]'), '') // Remove punctuation
        .split(RegExp(r'\s+'))
        .where((word) => word.isNotEmpty)
        .toList();
  }

  int getWordCount() => words.length;

  int getCharacterCountWithSpaces() => text.length;

  int getCharacterCountWithoutSpaces() => text.replaceAll(' ', '').length;

  double getAverageWordLength() {
    if (words.isEmpty) return 0;
    int totalLength = words.fold(0, (sum, word) => sum + word.length);
    return totalLength / words.length;
  }

  Map<String, int> getMostFrequentWords(int limit) {
    Map<String, int> wordCount = {};

    for (String word in words) {
      wordCount[word] = (wordCount[word] ?? 0) + 1;
    }

    // Sort by count (descending) and take top 'limit' words
    var sortedEntries = wordCount.entries.toList()
      ..sort((a, b) => b.value.compareTo(a.value));

    Map<String, int> result = {};
    for (int i = 0; i < limit && i < sortedEntries.length; i++) {
      result[sortedEntries[i].key] = sortedEntries[i].value;
    }

    return result;
  }
}
